import reimport urllib.request,sysdef getWeather():    provice = input('输入省份名（请使用拼音）：')    city = input('输入城市名（请使用拼音）：')    # 获取天气的url    url = "http://qq.ip138.com/weather/" + provice + '/' + city + '.htm'    # 获取页面信息    weatherhtml = urllib.request.urlopen(url)    res = weatherhtml.read().decode('gb2312')    pattern = 'Title.+<b>(.*)</b>'    Title = re.search(pattern, res).group(1)    pattern = '>(\d*-\d*-\d*.+?)<'    date = re.findall(pattern, res)    pattern = '<br/>(.*)</td>'    weather = re.findall(pattern, res)    print("%20s" % Title)    length = len(date)    for i in range(0, length):        print('%20s' % date[i], '\t%s' % weather[i])if __name__ == "__main__":    getWeather()# def get_weather():#     provice=input('请输入省份(请使用拼音):')#     city = input('输入城市名（请使用拼音）：')#     # 获取天气的url#     url = "http://qq.ip138.com/weather/" + provice + '/' + city + '_7tian.htm'#     # 获取页面信息#     weatherhtml = urllib.request.urlopen(url)#     res = weatherhtml.read().decode('GB2312')