import requestsimport jsonurl  = 'http://wthrcdn.etouch.cn/weather_mini?citykey=%s'% '101010100'response = requests.request("GET", url)a = eval(response.content)print('城市'+a.get('data').get('city'))print('未来几天的天气情况：')for day in a.get('data').get('forecast'):    print('日期:'+day['date'])    print('\t最高温度:' + day['high'])    print('\t风力:' + day['fengli'])    print('\t最低温度:' + day['low'])    print('\t风向:' + day['fengxiang'])    print('\t类型:' + day['type'])print('感冒：'+a.get('data').get('ganmao'))